import { useEffect, useState } from "react";

function MyFusions() {
    const [myPokeFusions, setMyPokeFusions] = useState([]);

    useEffect(() => {
        async function fetchMyFusions() {
            try {
                const res = await fetch('http://localhost:3001/my-fusions', {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (res.ok) {
                    const data = await res.json();
                    setMyPokeFusions(data);
                } else {
                    console.error("Brak fuzji lub błąd odpowiedzi");
                }
            } catch (error) {
                console.error("Błąd podczas pobierania fuzji:", error);
            }
        }

        fetchMyFusions();
    }, []);

    return (
        <div style={styles.grid}>
            {myPokeFusions.map((entry, index) => (
                <div key={index} style={styles.card}>
                    <img
                        src={`https://images.alexonsager.net/pokemon/fused/${entry.pokemon1}/${entry.pokemon1}.${entry.pokemon2}.png`}
                        alt="Fuzja"
                        style={styles.image}
                    />
                    <label>#{entry.pokemon1} + #{entry.pokemon2}</label>
                </div>
            ))}
        </div>
    );
}

const styles = {
    grid: {
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
        gap: '1rem',
        padding: '1rem',
    },
    card: {
        backgroundColor: '#f0f0f0',
        padding: '0.5rem',
        borderRadius: '8px',
        textAlign: 'center',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
    },
    image: {
        width: '100%',
        height: 'auto',
    }
};

export default MyFusions;
