import { useState } from 'react';
import './App.css';
import Login from './auth/Login';
import Register from './auth/Register';
import Fuse from './poke/Fuse';
import Logout from './auth/Logout';
import MyFusions from './poke/MyFusions';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [actualPage, setActualPage] = useState('login');
  const token = localStorage.getItem('token');
  const rand = Math.ceil(Math.random() * 1025);

  return (
    <div className="App">
      <header className="App-header">
        <img
          src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${rand}.png`}
          alt="Pokemon"
        />

        {token ? (
          <>
            <Fuse />
            <Logout />
            <MyFusions/>
          </>
        ) : actualPage === 'login' ? (
          <>
            <Login />
            <button onClick={() => setActualPage('register')}>
              Nie masz konta? Zarejestruj się
            </button>
          </>
        ) : (
          <>
            <Register />
            <button onClick={() => setActualPage('login')}>
              Masz konto? Zaloguj się
            </button>
          </>
        )}
      </header>
    </div>
  );
}

export default App;
