import { useState, useEffect } from 'react';
import './App.css';
import Login from './auth/Login';
import Register from './auth/Register';
import Fuse from './poke/Fuse';
import Logout from './auth/Logout';
import MyFusions from './poke/MyFusions';
import TopNavBar from './nav/TopNavBar';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activePage, setActivePage] = useState('fuse'); // fuse, myfusions, etc.
  const token = localStorage.getItem('token');
  const rand = Math.ceil(Math.random() * 1025);

  useEffect(() => {
    setIsLoggedIn(!!token);
  }, [token]);

  const renderContent = () => {
    switch (activePage) {
      case 'fuse':
        return <Fuse />;
      case 'myfusions':
        return <MyFusions />;
      case 'logout':
        localStorage.removeItem("token")
        window.location.reload()
      case 'premium':
        return <h2 style={{ marginTop: "2rem" }}>Premium coming soon...</h2>;
      default:
        return <Fuse />;
    }
  };

  return (
    <div className="App">
      <TopNavBar setActivePage={setActivePage} token={token}/>
      {renderContent()}
      <MyFusions />
    </div>
  );
}

export default App;
