import { useEffect, useState } from 'react';

function Fuse() {
    const [pokeList, setPokeList] = useState([]);
    const [selected1, setSelected1] = useState('1');
    const [selected2, setSelected2] = useState('1');
    const [fusionContent, setFusionContent] = useState(null);
    const [lastFusionDate, setLastFusionDate] = useState(null);

    useEffect(() => {
        async function fetchPokemonList() {
            try {
                const res = await fetch('https://pokeapi.co/api/v2/pokemon?limit=100000');
                const data = await res.json();

                const pokemonsWithId = data.results.map((pokemon) => {
                    // wyciągnij id z URL-a
                    const idMatch = pokemon.url.match(/\/pokemon\/(\d+)\//);
                    const id = idMatch ? parseInt(idMatch[1]) : null;

                    return { ...pokemon, id }; // dodaj id do obiektu
                });

                setPokeList(pokemonsWithId);
            } catch (err) {
                console.error('Błąd przy pobieraniu Pokémonów:', err);
            }
        }
        async function fetchLastFusion() {
            const res = await fetch('http://localhost:3001/last-fusion', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });

            if (!res.ok) {
                setLastFusionDate("Spróbuj swojej pierwszej fuzji!")
            } else {
                const data = await res.json();
                setLastFusionDate(data.date);
            }
        }

        fetchLastFusion();
        fetchPokemonList();
    }, []);

    async function fuseSelected() {
        const token = localStorage.getItem('token');
        if (!token) return alert("Zaloguj się, aby wykonać fuzję.");

        const pokemon1 = pokeList.find(p => p.id === parseInt(selected1));
        const pokemon2 = pokeList.find(p => p.id === parseInt(selected2));
        
        const fused = (
            <div>
                <img src={`https://images.alexonsager.net/pokemon/fused/${selected1}/${selected1}.${selected2}.png`} />
                <label>{pokemon1?.name.slice(0, 3)}{pokemon2?.name.slice(-3)}</label>
            </div>
        );
    
        setFusionContent(fused);
    
        try {
            const res = await fetch('http://localhost:3001/fuse', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({ pokemon1: selected1, pokemon2: selected2 })
            });
    
            if (!res.ok) {
                console.error('Nie udało się zapisać fuzji');
            } else {
                const fusionData = await res.json();
                setLastFusionDate(fusionData.date || new Date().toISOString());
            }
        } catch (err) {
            console.error('Błąd zapisu fuzji:', err);
        }
    }
    

    return (
        <div>
            {lastFusionDate && (
                <div>Twoja ostatnia fuzja: {new Date(lastFusionDate).toLocaleString()}</div>
            )}
            <div>
                <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${selected1}.png`}></img>
                <select value={selected1} onChange={(e) => setSelected1(e.target.value)}>
                    {pokeList.map((pokemon, index) => (
                        <option key={index} value={pokemon.id}>
                            #{pokemon.id} {pokemon.name}
                        </option>
                    ))}
                </select>
            </div>

            <div>
                <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${selected2}.png`}></img>
                <select value={selected2} onChange={(e) => setSelected2(e.target.value)}>
                    {pokeList.map((pokemon, index) => (
                        <option key={index} value={pokemon.id}>
                            #{pokemon.id} {pokemon.name}
                        </option>
                    ))}
                </select>
            </div>
            <button onClick={fuseSelected}>Fuse</button>
            {fusionContent}
        </div>
    );
}

export default Fuse;
