import { useState, useEffect } from 'react';
import './App.css';
import Login from './auth/Login';
import Register from './auth/Register';
import Fuse from './poke/Fuse';
import Logout from './auth/Logout';
import MyFusions from './poke/MyFusions';
import TopNavBar from './nav/TopNavBar';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activePage, setActivePage] = useState('fuse'); // fuse, myfusions, etc.
  const token = localStorage.getItem('token');
  const rand = Math.ceil(Math.random() * 1025);

  useEffect(() => {
    setIsLoggedIn(!!token);
  }, [token]);

  const renderContent = () => {
    if (!isLoggedIn) {
      return activePage === 'register' ? (
        <>
          <Register />
          <button onClick={() => setActivePage('login')}>Masz konto? Zaloguj się</button>
        </>
      ) : (
        <>
          <Login />
          <button onClick={() => setActivePage('register')}>Nie masz konta? Zarejestruj się</button>
        </>
      );
    }

    switch (activePage) {
      case 'fuse':
        return <Fuse />;
      case 'myfusions':
        return <MyFusions />;
      case 'logout':
        return <Logout />;
      case 'premium':
        return <h2 style={{ marginTop: "2rem" }}>Premium coming soon...</h2>;
      default:
        return <Fuse />;
    }
  };

  return (
    <div className="App">
      <TopNavBar setActivePage={setActivePage} />

      <header className="App-header">
        <img
          src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${rand}.png`}
          alt="Pokemon"
        />
      </header>

      {renderContent()}
    </div>
  );
}

export default App;
