import { useState } from 'react';
import './App.css';
import Login from './auth/Login';
import Register from './auth/Register';

function App() {
  const [actualPage, setActualPage] = useState('login'); // 'login' lub 'register'

  // Funkcje zmieniające stan
  const showLogin = () => setActualPage('login');
  const showRegister = () => setActualPage('register');
  let rand = Math.ceil(Math.random()*1025);
  return (
    <div className="App">
      <header className="App-header">
      <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${rand}.png`} alt="Pokemon" />
        {actualPage === 'login' ? <Login /> : <Register />}
        <button onClick={showRegister}>Register form</button><br/>
        <button onClick={showLogin}>Login form</button>
      </header>
    </div>
  );
}

export default App;
