import { useEffect, useState } from "react";
import { Container, Row, Col, Card } from "react-bootstrap";

function MyFusions() {
    const [myPokeFusions, setMyPokeFusions] = useState([]);

    useEffect(() => {
        async function fetchMyFusions() {
            try {
                const res = await fetch('http://localhost:3001/my-fusions', {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (res.ok) {
                    const data = await res.json();
                    setMyPokeFusions(data.fusions);
                    console.log("Pobrano fuzje!:", data.fusions);
                } else {
                    console.error("Brak fuzji lub błąd odpowiedzi");
                }
            } catch (error) {
                console.error("Błąd podczas pobierania fuzji:", error);
            }
        }

        fetchMyFusions();
    }, []);

    return (
        <Container className="mt-4">
            <Row>
                {Array.isArray(myPokeFusions) && myPokeFusions.map((entry, index) => (
                    <Col key={index} xs={12} sm={6} md={4} lg={3} className="mb-4">
                        <Card className="text-center">
                            <Card.Img
                                variant="top"
                                src={`https://images.alexonsager.net/pokemon/fused/${entry.pokemon1}/${entry.pokemon1}.${entry.pokemon2}.png`}
                                alt="Fuzja Pokémona"
                            />
                            <Card.Body>
                                <Card.Title>
                                    #{entry.pokemon1} + #{entry.pokemon2}
                                </Card.Title>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
}

export default MyFusions;
