import './App.css';
import Login from './auth/Login';
import Register from './auth/Register';
import Fuse from './poke/Fuse';
import Logout from './auth/Logout';
import MyFusions from './poke/MyFusions';
import TopNavBar from './nav/TopNavBar';
import { Routes, Route, Navigate } from "react-router-dom";
import { useEffect, useState } from 'react';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const token = localStorage.getItem('token');
  const rand = Math.ceil(Math.random() * 1025);

  useEffect(() => {
    setIsLoggedIn(!!token);
  }, [token]);

  return (
    <div className="App">
        <TopNavBar />

        <header className="App-header">
          <img
            src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${rand}.png`}
            alt="Pokemon"
          />
        </header>

        <Routes>
          {!isLoggedIn ? (
            <>
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="*" element={<Navigate to="/login" replace />} />
            </>
          ) : (
            <>
              <Route path="/fuse" element={<Fuse />} />
              <Route path="/myfusions" element={<MyFusions />} />
              <Route path="/logout" element={<Logout />} />
              <Route path="/premium" element={<h2 style={{ marginTop: "2rem" }}>Coming soon...</h2>} />
              <Route path="*" element={<Navigate to="/fuse" replace />} />
            </>
          )}
        </Routes>
    </div>
  );
}

export default App;
