import { useEffect, useState } from 'react';

function Fuse() {
    const [pokeList, setPokeList] = useState([]);
    const [selected1, setSelected1] = useState('1');
    const [selected2, setSelected2] = useState('1');
    const [fusionContent, setFusionContent] = useState(null);

    useEffect(() => {
        async function fetchPokemonList() {
            try {
                const res = await fetch('https://pokeapi.co/api/v2/pokemon?limit=100000');
                const data = await res.json();

                const pokemonsWithId = data.results.map((pokemon) => {
                    // wyciągnij id z URL-a
                    const idMatch = pokemon.url.match(/\/pokemon\/(\d+)\//);
                    const id = idMatch ? parseInt(idMatch[1]) : null;

                    return { ...pokemon, id }; // dodaj id do obiektu
                });

                setPokeList(pokemonsWithId);
            } catch (err) {
                console.error('Błąd przy pobieraniu Pokémonów:', err);
            }
        }

        fetchPokemonList();
    }, []);

    function fuseSelected() {
        const fused = (
            <img src={`https://images.alexonsager.net/pokemon/fused/${selected1}/${selected1}.${selected2}.png`}></img>
        )
        setFusionContent(fused);
    }

    return (
        <div>
            <div>
                <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${selected1}.png`}></img>
                <select value={selected1} onChange={(e) => setSelected1(e.target.value)}>
                    {pokeList.map((pokemon, index) => (
                        <option key={index} value={pokemon}>
                            {pokemon.name}
                        </option>
                    ))}
                </select>
            </div>

            <div>
                <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${selected2}.png`}></img>
                <select value={selected2} onChange={(e) => setSelected2(e.target.value)}>
                    {pokeList.map((pokemon, index) => (
                        <option key={index} value={pokemon}>
                            {pokemon.name}
                        </option>
                    ))}
                </select>
            </div>
            <button onClick={fuseSelected}>Fuse</button>
            <div id="fusion">
                {fusionContent}
            </div>
        </div>
    );
}

export default Fuse;
