import logo from './logo.svg';
import './App.css';
import Login from './auth/Login';
import Register from './auth/Register';

let actualPage = <Login/>

function showLogin(){
  actualPage = <Register/>
}
function showRegister(){
  actualPage = <Login/>
}

function App() {
  const [actualPage, setActualPage] = useState('login'); // 'login' lub 'register'

  // Funkcje zmieniające stan
  const showLogin = () => setActualPage('login');
  const showRegister = () => setActualPage('register');
  return (
    <div className="App">
      <header className="App-header">
        <img src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png" alt="Bulbasaur" />
        {actualPage}
        <button onClick={showRegister}>
          Register form
        </button>
        <button onClick={showLogin}>
          Login from
        </button>
      </header>
    </div>
  );
}

export default App;
